import React from 'react'
import styled from 'styled-components'
import Navbar from '../components/Navbar';
import Announcement from '../components/Announcement';
import Newsletter from '../components/Newsletter';
import Footer from '../components/Footer';


const Container=styled.div``;
const Wrapper=styled.div`
padding: 50px;
display: flex;
`;
const ImgContainer=styled.div`
flex:1;

`;

const Image=styled.img`
width: 100%;
height:90vh;
object-fit:cover;
`;

const InfoContainer=styled.div`
flex:1;
padding: 0px 50px;
`;

const Title=styled.h1`
font-weight:200;
`;

const Desc=styled.p`
margin: 20px 0px;
`;

const Price=styled.span`
font-weight:200;
font-size:40px;
`;

const FilterContainer=styled.div`
display : flex;
justify-content:space-between;
width:50%;
margin:30px 0px;
`;
const Filter=styled.div``;
const FilterTitle=styled.span``;
const FilterColor=styled.div``;
const FilterSize=styled.select``;
const FilterSizeOption=styled.option``;

const Product = () => {
  return (
    <Container>
        <Navbar/>
        <Announcement/>
        <Wrapper>
            <ImgContainer>
            <Image src="https://i.ibb.co/q0MxV7X/hoodie.jpg"/>
            </ImgContainer>
            <InfoContainer>
                <Title>Relaxed Fit Hoodie</Title>
                <Desc>Our hoodie combines fashion-forward design with ultimate comfort, making it a go-to piece for any season. Crafted with the highest quality materials, it offers a soft and plush feel that wraps you in warmth and relaxation.</Desc>
                <Price>₹ 2500</Price>
                <FilterContainer>
                    <Filter>
                        <FilterTitle>Color</FilterTitle>
                        <FilterColor color="black"/>
                        <FilterColor color="white"/>
                        <FilterColor color="beige"/>
                        <FilterColor color="pink"/>
                    </Filter>
                    <Filter>
                    <FilterTitle>Size</FilterTitle>
                       <FilterSize>
                        <FilterSizeOption>XS</FilterSizeOption>
                        <FilterSizeOption>S</FilterSizeOption>
                        <FilterSizeOption>M</FilterSizeOption>
                        <FilterSizeOption>L</FilterSizeOption>
                        <FilterSizeOption>XL</FilterSizeOption>
                       </FilterSize>
                    </Filter>
                </FilterContainer>
            </InfoContainer>
        </Wrapper>
        <Newsletter/>
        <Footer/>
    </Container>
  )
}

export default Product
